import HeaderView from './Header.vue'
import FooterView from './Footer.vue'
import { useRouter } from 'vue-router'
const router = useRouter()
const link = ['Presensi', 'Catalog', 'Twibbon', 'Feedback']

const changeview = (link) => {
  if (link === 'Presensi') {
    router.push('/presensi')
  } else if (link === 'Catalog') {
    window.open('https://vensky.cloud/catalog', '_blank')
  } else if (link === 'Twibbon') {
    window.open('https://vensky.cloud/twibbon', '_blank')
  } else if (link === 'Feedback') {
    router.push('/feedback')
  }
}
</script>

